import { styled } from "styled-components";
import ProfileComponent from "./Profile";
import DetailInfoComponent from "./DetailInfo";
import HobbyComponent from "./Hobby";
import nangmanImg from "../assests/nangman.jpeg";
import cwImg from "../assests/CW.jpeg";
import wbImg from "../assests/WB.jpeg";

const IntroBody = styled.div`
  background: #F3F5FF;
`

const CategoryText = styled.p`
  font-family: Inter;
  font-size: 5vw;
  font-weight: 800;
  // line-height: 84.72px;
  text-align: center;
  color: #4A86FF;
  margin: 0;
  padding-top: 5vh;

  @media (max-width: 450px) {
    padding-top: 3vh;
  }
`

const ProfileContainer = styled.div`
  display: flex;
  justify-content: center;
  gap: 5vw;
  margin-top: 3vh;
  padding-left: 5vw;
  padding-right: 5vw;
  margin-bottom: 15vh;

  @media (max-width: 450px) {
    margin-bottom: 5vh;
  }
`

const HobbyContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: 5vh;
  gap: 2vw;

  @media(max-width: 450px) {
    margin-top: 1.5vh;
  }
`

export default function MainIntroBox() {
  return (
    <IntroBody>
      <CategoryText>ABOUT</CategoryText>
      <ProfileContainer>
        <ProfileComponent />
        <DetailInfoComponent />
      </ProfileContainer>
      <CategoryText>Hobby</CategoryText>
      <HobbyContainer>
        <HobbyComponent text="한강 코딩" img={nangmanImg}/>
        <HobbyComponent text="카페에서 작업하기" img={cwImg}/>
        <HobbyComponent text="워라벨" img={nangmanImg}/>
      </HobbyContainer>
    </IntroBody>
  )
}
